import { createStore } from 'vuex'
export default createStore({
  state() {
    return {
      count: 0,
      cart: [],
    }
  },
  getters: {
  },
  mutations: {

    addToCart(state, item) {
      let found = state.cart.find(product => product.key == item.key);

      state.cart.push(item);

      this.state.cart.set(item, 'quantity', 1);
      this.state.cart.set(item, 'totalPrice', item.price);

      state.count++;
      console.log(item);
    }
  }
  ,
  actions: {
  },
  modules: {
  }
})
